import React, { Component } from "react";
import { FaGithubAlt, FaPlus, FaSpinner } from "react-icons/fa";
import { Container, Form, SubmitButton, List } from "./styles";
import api from "../../services/api";

export default class Main extends Component {
  state = {
    newRepo: "",
    repositories: [],
    laoding: false
  };

  handleInputChange = e => {
    this.setState({ newRepo: e.target.value });
  };

  handleSubmit = async e => {
    e.preventDefault();
    //Atribui TRUE ao state para desbilitar botão ao executar função.
    this.setState({ laoding: true });
    //Seta os estados.
    const { newRepo, repositories } = this.state;
    //Cria URL pra buscar na api do github.
    //[WARNING] Verificar PQ a api.get não ta buscando o baseUrl  
    const urlNewRepo = "http://api.github.com/repos/" + newRepo;
    //Faz a busca na api do github.
    const response = await api.get(urlNewRepo);
    //Cria objeto para guardar as informações que for necessária.
    const data = {
      name: response.data.full_name
    };

    this.setState({
      repositories: [...repositories, data],
      newRepo: "",
      laoding: false
    });
  };

  render() {
    const { newRepo, laoding, repositories } = this.state;

    return (
      <Container>
        <h1>
          <FaGithubAlt />
          Repositórios
        </h1>
        <Form onSubmit={this.handleSubmit}>
          <input
            type="text"
            placeholder="Adicionar Repositório"
            value={newRepo}
            onChange={this.handleInputChange}
          />
          <SubmitButton laoding={laoding}>
            { laoding ? <FaSpinner size={14} /> :  <FaPlus size={14} /> }
          </SubmitButton>
        </Form>

        <List>
          {repositories.map(repository => (
            <li key={repository.name}>
              <span>{repository.name}</span>
              <a href="#">Detalhes</a>
            </li>
          ))}
        </List>
      </Container>
    );
  }
}
