import Styled, { keyframes, css } from "styled-components";

export const Container = Styled.div` 
  max-width: 700px;
  background: #fff;
  border-radius: 4px;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
  padding: 30px;
  margin: 80px auto;

  h1 {
    font-size: 20px;
    display: flex;
    flex-direction: row;
    align-items: center;
    svg{
      margin-right: 10px;
    }
  }
`;
export const Form = Styled.form`
  margin-top: 30px;
  display: flex;
  flex-direction: row;
  align-items: center;

  input{
    flex: 1;
    border: 1px solid #eee;
    padding: 10px 15px;
    border-radius: 4px;
    font-size: 16px; 
  }
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg)
  }
  to {
    transform: rotate(360deg)
  }
`;

export const SubmitButton = Styled.button.attrs(props => ({
  type: "submit",
  disabled: props.laoding,
}))`
  color: #fff;
  background: #303030;
  border: 0;
  padding: 15px 15px;
  margin-left: 10px;
  border-radius: 4px; 

  display: flex;
  flex-direction: center;
  align-items: center;


  &[disabled] { 
    cursor:  not-allowed;
    opacity: 0.6;
  }
`;
